mapper.select:
select a.`create_userid` as userId, d.username as "userName", d.shop_name as "shopName", b.item_id as itemId, c.title as itemName, sum(b.num) as num from sc_ship_order a left join sc_ship_order_detail b on b.order_id = a.id left join sc_item c on b.item_id = c.id left join sc_user d on d.id = a.create_userid where a.type='send' and (a.status='SEND_FINISH' or a.status='WAIT_BUYER_RECEIVED')
and a.`create_userid`= #{userId}
and a.`create_date` > date_format(#{startDate},'%Y-%m-%d %H:%i') and a.`create_date` <= date_format(#{endDate},'%Y-%m-%d %H:%i') group by b.item_id

SELECT count(1) FROM sc_ship_order a WHERE a.type='send' and (a.status='SEND_FINISH' or a.status='WAIT_BUYER_RECEIVED')
and a.`create_userid`= #{userId}
and a.`create_date` > date_format(#{startDate},'%Y-%m-%d %H:%i') and a.`create_date` <= date_format(#{endDate},'%Y-%m-%d %H:%i')

SELECT b.shop_name as "createUser.shopName", create_date as "createDate", a.id, a.centro_id as centroId, a.origin_persion as "originPersion", a.origin_phone as "originPhone", a.receiver_address as "receiverAddress", a.receiver_city as "receiverCity", a.receiver_state as "receiverState", a.receiver_name as "receiverName", a.receiver_mobile as "receiverMobile", a.receiver_phone as "receiverPhone", a.receiver_district as "receiverDistrict", a.receiver_zip as "receiverZip", a.seller_memo as "sellerMemo", a.seller_mobile as "sellerMobile", a.seller_phone as "sellerPhone", a.buyer_nick as "buyerNick", a.buyer_memo as "buyerMemo", a.buyer_message as "buyerMessage" , a.last_update_date as "lastUpdateDate", a.remark, a.orderno, a.express_company as "expressCompany", a.express_orderno as "expressOrderno" FROM sc_ship_order a LEFT JOIN sc_user b ON a.create_userid = b.id WHERE a.type='send' and (a.status='SEND_FINISH' or a.status='WAIT_BUYER_RECEIVED')
and a.`create_userid`= #{userId}
and a.`create_date` > date_format(#{startDate},'%Y-%m-%d %H:%i') and a.`create_date` <= date_format(#{endDate},'%Y-%m-%d %H:%i')
limit #{start},
#{offset}
