mapper.select:
SELECT T.*
FROM T_xinyu_ShipOrder T
where 1=1

	and T.ForderCode=#{orderCode}


	and T.FKuserID=#{userId}


	and T.ForderType=#{orderType}


	and T.FKreceiverInfoID=#{receiverInfoId}


	and T.Fcu=#{cu}

select t.id as "orderId",t.express_code as "expressCode",t.express_orderno as "orderno",t.items as "items"
from store.sc_ship_order t
inner join store.sc_user u on t.create_userid = u.id
where 1=1

	and t.express_orderno = #{code}


	and T.Fcu=#{cu}

SELECT
count(1) FROM t_xinyu_shiporder where ForderCode=#{orderCode} and
FKuserID=#{userId};

SELECT o.fid as tmsId, o.FKorderID, o.Fcode, o.ForderCode, o.Fitems, o.ForderStatus, t.ForderPayTime, t.Fid as orderId, t.ForderShopCreateTime, t.FKuserID, t.FstoreCode, t.ForderCode as shipOrderCode, t.FerpOrderCode, t.ForderType, t.FshopName, t.FtmsServiceCode, t.FtmsServiceName, t.FtmsOrderCode, t.Fremark, t.FbuyerMessage, t.FsellerMessage, t.fstatus, t.fcreateTime, t.fsplitStatus, t.FtotalWeight, t.fitems, t.FitemGroupStatus, t.FtmsDisplayName, t.FlastUpdateTime, o.FbatchCode, o.FpackageWeight, t.FKreceiverInfoID FROM t_xinyu_tmsorder o inner join T_xinyu_ShipOrder T on o.fkorderid=t.fid
