SELECT d.FKitemID AS itemId, count(1) AS quantity FROM t_xinyu_shiporder t INNER JOIN t_xinyu_wmsconsignorderitem d ON d.FKorderID = t.fid
1=1
and t.FcreateTime>=DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i')
and t.FcreateTime<=DATE_FORMAT(#{endDate},'%Y-%m-%d %H:%i')
and t.FKuserID in(${userIds})
and t.`Fstatus`=#{status}
and d.FKitemID=#{itemId}
and t.Fcu=#{cu}
GROUP BY d.FKitemID order BY t.FKuserID,quantity desc

