FtmsServiceCode=#{tmsServiceCode},FtmsServiceName=#{tmsServiceName},FtmsOrderCode=#{tmsOrderCode}, fstatus=#{status}, FKuserID=#{user.id}, FstoreCode=#{storeCode}, ForderCode=#{orderCode}, FerpOrderCode=#{erpOrderCode}, ForderType=#{orderType}, FshopId=#{shopId}, FshopName=#{shopName}, ForderFlag=#{orderFlag}, ForderSource=#{orderSource}, ForderCreateTime=#{orderCreateTime}, ForderPayTime=#{orderPayTime}, FpayNo=#{payNo}, ForderExaminationTime=#{orderExaminationTime}, ForderShopCreateTime=#{orderShopCreateTime}, ForderAmount=#{orderAmount}, FdiscountAmount=#{discountAmount}, FarAmount=#{arAmount}, FgotAmount=#{gotAmount}, Fpostfee=#{postfee}, FserviceFee=#{serviceFee}, FprevOrderCode=#{prevOrderCode}, FprevErpOrderCode=#{prevErpOrderCode}, FKdeliverRequirementsID=#{deliverRequirements.id}, FKreceiverInfoID=#{receiverInfo.id}, FKuniReceiverInfoID=#{uniReceiverInfo.id}, FKsenderInfoID=#{senderInfo.id}, FtimeZone=#{timeZone}, Fcurrency=#{currency}, Fremark=#{remark}, FbuyerMessage=#{buyerMessage}, FsellerMessage=#{sellerMessage}, FextendFields=#{extendFields}, FsplitStatus=#{splitStatus}, FtemplateURL=#{templateURL}, FtmsRouteCode=#{tmsRouteCode}, FsortationName=#{sortationName}, FconsolidationName=#{consolidationName}, FconsolidationCode=#{consolidationCode}, FbatchCode=#{batchCode}, FlastUpdateTime=#{lastUpdateTime}, Fitems=#{items}, FitemGroupStatus=#{itemGroupStatus}, Fcu=#{cu}, FtmsDisplayName=#{tmsDisplayName},
where fid=#{id}

