mapper.select:
select a.ForderId as "orderId",a.FuserId as "userId",a.FcreateDate as "createDate",a.FtmsOrderCode as "tmsOrderCode", a.FbackOrderCode as "backOrderCode",a.Fdescription as "description",GROUP_CONCAT(a.items) as "items" from( select s.ForderId,s.FID,s.FuserId,s.FcreateDate,s.FtmsOrderCode,s.FbackOrderCode,s.Fdescription,CONCAT(i.FitemName,":",i.Fquantity,"件;") as "items" from t_xinyu_shiporderback s inner join t_xinyu_orderbackitem i on s.FID = i.FKorderBackId where 1=1
and s.FuserId=#{userId}
and (s.FbackOrderCode=#{q} or s.FtmsOrderCode=#{q})
and s.Fcu=#{cu}
and s.`FcreateDate` > date_format(#{startDate},'%Y-%m-%d %H:%i') and s.`FcreateDate` <= date_format(#{endDate},'%Y-%m-%d %H:%i')
)a group by a.FID

SELECT T.*
FROM T_xinyu_ShipOrderBack T
where 1=1

	and T.ForderId=#{orderId}


	and T.FID=#{id}


	and T.FuserId=#{userId}


	and T.FtmsOrderCode=#{tmsOrderCode}


	and T.FbackOrderCode=#{backOrderCode}


	and T.Fcu=#{cu}

		SELECT T.*
		FROM T_xinyu_ShipOrderBack T
		where 1=1
		
			and T.ForderId=#{orderId}
		
		
			and T.FuserId=#{userId}
		
		
			and T.FtmsOrderCode=#{tmsOrderCode}
		
		
			and T.FbackOrderCode=#{backOrderCode}
		
		
			and (T.FbackOrderCode=#{q} or T.FtmsOrderCode=#{q})
		
		
			and T.Fcu=#{cu}
		
		  
    		
				and t.`FcreateDate` > date_format(#{startDate},'%Y-%m-%d %H:%i')
				and t.`FcreateDate` <= date_format(#{endDate},'%Y-%m-%d %H:%i')
			
		
		
			limit #{pageNum},#{pageSize}

		SELECT count(1)
		FROM T_xinyu_ShipOrderBack T
		where 1=1
		
			and T.ForderId=#{orderId}
		
		
			and T.FuserId=#{userId}
		
		
			and T.FtmsOrderCode=#{tmsOrderCode}
		
		
			and T.FbackOrderCode=#{backOrderCode}
		
		
			and (T.FbackOrderCode=#{q} or T.FtmsOrderCode=#{q})
		
		
			and T.Fcu=#{cu}
		
		  
    		
				and t.`FcreateDate` > date_format(#{startDate},'%Y-%m-%d %H:%i')
				and t.`FcreateDate` <= date_format(#{endDate},'%Y-%m-%d %H:%i')

mapper.update:
update T_xinyu_ShipOrderBack
Fcu=#{cu}, ForderId=#{orderId}, FuserId=#{userId}, FtmsOrderCode=#{tmsOrderCode}, FbackOrderCode=#{backOrderCode}, FKacoountId=#{createBy.id}, FcreateDate = now()
where fid=#{id}
